-- FOR TESTING USING DERBY (NOTE: SOME DATA TYPES ARE DIFFERENT THEN FROM MYSQL)
CREATE TABLE sidora.camelBatchRequests ("correlationId" CHAR(36) NOT NULL, "parentId" VARCHAR(255), "resourceFileList" VARCHAR(255), "ds_metadata" VARCHAR(255), "ds_sidora" VARCHAR(255), "association" VARCHAR(255), "resourceOwner" VARCHAR(255), "codebookPID" VARCHAR(255) DEFAULT NULL, "resourceCount" INT DEFAULT NULL, "processCount" INT NOT NULL DEFAULT 0, "request_consumed" BOOLEAN NOT NULL DEFAULT false, "request_complete" BOOLEAN NOT NULL DEFAULT false, "created" TIMESTAMP DEFAULT CURRENT_TIMESTAMP, "updated" TIMESTAMP DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY ("correlationId"));

CREATE TABLE sidora.camelBatchResources ("correlationId" CHAR(36) NOT NULL, "resourceFile" VARCHAR(255), "objDsLabel" VARCHAR(255), "parentId" VARCHAR(255), "pid" VARCHAR(255) DEFAULT NULL, "contentModel" VARCHAR(255) DEFAULT NULL, "resourceOwner" VARCHAR(255), "titleLabel" VARCHAR(255) DEFAULT NULL, "resource_created" BOOLEAN NOT NULL DEFAULT false, "ds_relsExt_created" BOOLEAN NOT NULL DEFAULT false, "ds_metadata_created" BOOLEAN NOT NULL DEFAULT false, "ds_sidora_created" BOOLEAN NOT NULL DEFAULT false, "ds_dc_created" BOOLEAN NOT NULL DEFAULT false, "ds_obj_created" BOOLEAN NOT NULL DEFAULT false, "ds_tn_created" BOOLEAN NOT NULL DEFAULT false, "codebook_relationship_created" BOOLEAN NOT NULL DEFAULT false, "parent_child_resource_relationship_created" BOOLEAN NOT NULL DEFAULT false, "resource_consumed" BOOLEAN NOT NULL DEFAULT false, "resource_complete" BOOLEAN NOT NULL DEFAULT false, "created_date" TIMESTAMP DEFAULT CURRENT_TIMESTAMP, "updated_date" TIMESTAMP DEFAULT CURRENT_TIMESTAMP);





